mapper.select:
select sum(url_clicked) as total
  from toc_manufacturers_info
 where manufacturers_id = #{id}

mapper.update:
update toc_manufacturers

   	manufacturers_name = #{name},
   	manufacturers_image = #{image},
   	last_modified = #{dateUpdated}
 
 where manufacturers_id = #{id}

update toc_manufacturers_info

   	manufacturers_url = #{url},
   	manufacturers_friendly_url = #{friendlyUrl},
   	manufacturers_page_title = #{pageTitle},
   	manufacturers_meta_keywords = #{metaKeywords},
   	manufacturers_meta_description = #{metaDescription}
 
 where manufacturers_id = #{id}
   and languages_id = #{languageId}
